document.addEventListener("DOMContentLoaded",()=>{mobileMenu=document.querySelector(".mobile-menu"),document.querySelector("#mobile-menu-btn").addEventListener("click",()=>{document.querySelector(".mobile-next-to").classList.toggle("nav-show"),mobileMenu.classList.toggle("menu-toggle"),document.querySelector("body").classList.toggle("no-scroll"),document.querySelector(".navbar").classList.toggle("fixed-top")}),document.querySelector(".mobile-next-to").addEventListener("click",()=>{document.querySelector(".mobile-next-to").classList.remove("show"),mobileMenu.classList.remove("menu-toggle"),document.querySelector(".navbar").classList.remove("fixed-top"),document.querySelector("body").classList.remove("no-scroll")})});const delay=e=>new Promise(o=>setTimeout(o,e));